func TestSigner(t *testing.T) {
	require := require.New(t)
	ctrl := gomock.NewController(t)
	client := mock_ioctlclient.NewMockClient(ctrl)
	client.EXPECT().SelectTranslation(gomock.Any()).Return("mockTranslationString", config.English).Times(2)
	client.EXPECT().SetEndpointWithFlag(gomock.Any())
	client.EXPECT().SetInsecureWithFlag(gomock.Any())

	t.Run("returns signer's address", func(t *testing.T) {
		client.EXPECT().AddressWithDefaultIfNotExist(gomock.Any()).Return("test", nil).AnyTimes()

		cmd := NewActionCmd(client)
		registerSignerFlag(client, cmd)
		_, err := util.ExecuteCmd(cmd, "--signer", "test")
		require.NoError(err)
		signer, err := cmd.Flags().GetString(signerFlagLabel)
		require.NoError(err)
		result, err := Signer(client, signer)
		require.NoError(err)
		require.Equal(result, "test")
	})
}

func TestSendRaw(t *testing.T) {
	require := require.New(t)
	ctrl := gomock.NewController(t)
	client := mock_ioctlclient.NewMockClient(ctrl)
	apiServiceClient := mock_iotexapi.NewMockAPIServiceClient(ctrl)
	selp := &iotextypes.Action{}

	client.EXPECT().SelectTranslation(gomock.Any()).Return("mockTranslationString", config.English).Times(3)
	client.EXPECT().APIServiceClient().Return(apiServiceClient, nil).Times(7)

	for _, test := range []struct {
		endpoint string
		insecure bool
	}{
		{
			endpoint: "111:222:333:444:5678",
			insecure: false,
		},
		{
			endpoint: "",
			insecure: true,
		},
	} {
		callbackEndpoint := func(cb func(*string, string, string, string)) {
			cb(&test.endpoint, "endpoint", test.endpoint, "endpoint usage")
		}
		callbackInsecure := func(cb func(*bool, string, bool, string)) {
			cb(&test.insecure, "insecure", !test.insecure, "insecure usage")
		}
		client.EXPECT().SetEndpointWithFlag(gomock.Any()).Do(callbackEndpoint).AnyTimes()
		client.EXPECT().SetInsecureWithFlag(gomock.Any()).Do(callbackInsecure).AnyTimes()

		t.Run("sends raw action to blockchain", func(t *testing.T) {
			response := &iotexapi.SendActionResponse{}

			apiServiceClient.EXPECT().SendAction(gomock.Any(), gomock.Any()).Return(response, nil).Times(3)

			cmd := NewActionCmd(client)
			_, err := util.ExecuteCmd(cmd)
			require.NoError(err)

			t.Run("endpoint iotexscan", func(t *testing.T) {
				client.EXPECT().Config().Return(config.Config{
					Explorer: "iotexscan",
					Endpoint: "testnet1",
				}).Times(2)

				err = SendRaw(client, cmd, selp)
				require.NoError(err)
			})

			t.Run("endpoint iotxplorer", func(t *testing.T) {
				client.EXPECT().Config().Return(config.Config{
					Explorer: "iotxplorer",
				}).Times(2)

				err := SendRaw(client, cmd, selp)
				require.NoError(err)
			})

			t.Run("endpoint default", func(t *testing.T) {
				client.EXPECT().Config().Return(config.Config{
					Explorer: "test",
				}).Times(2)

				err := SendRaw(client, cmd, selp)
				require.NoError(err)
			})
		})
	}

	t.Run("failed to invoke SendAction api", func(t *testing.T) {
		expectedErr := errors.New("failed to invoke SendAction api")
		apiServiceClient.EXPECT().SendAction(gomock.Any(), gomock.Any()).Return(nil, expectedErr)

		cmd := NewActionCmd(client)
		_, err := util.ExecuteCmd(cmd)
		require.NoError(err)
		err = SendRaw(client, cmd, selp)
		require.Contains(err.Error(), expectedErr.Error())
	})
}

func TestSendAction(t *testing.T) {
	require := require.New(t)
	ctrl := gomock.NewController(t)
	client := mock_ioctlclient.NewMockClient(ctrl)
	apiServiceClient := mock_iotexapi.NewMockAPIServiceClient(ctrl)
	passwd := "123456"

	ks := keystore.NewKeyStore(t.TempDir(), 2, 1)
	acc, err := ks.NewAccount(passwd)
	require.NoError(err)
	accAddr, err := address.FromBytes(acc.Address.Bytes())
	require.NoError(err)

	chainMetaResponse := &iotexapi.GetChainMetaResponse{ChainMeta: &iotextypes.ChainMeta{}}
	elp := createEnvelope(0)
	cost, err := elp.Cost()
	require.NoError(err)
	accountResponse := &iotexapi.GetAccountResponse{AccountMeta: &iotextypes.AccountMeta{
		Address:      accAddr.String(),
		PendingNonce: 1,
		Balance:      cost.String(),
	}}

	client.EXPECT().SelectTranslation(gomock.Any()).Return("action", config.English).AnyTimes()
	client.EXPECT().SetEndpointWithFlag(gomock.Any()).AnyTimes()
	client.EXPECT().SetInsecureWithFlag(gomock.Any()).AnyTimes()
	client.EXPECT().IsCryptoSm2().Return(false).AnyTimes()
	client.EXPECT().NewKeyStore().Return(ks).AnyTimes()

	t.Run("failed to get privateKey", func(t *testing.T) {
		expectedErr := errors.New("failed to get privateKey")
		client.EXPECT().ReadSecret().Return("", expectedErr).Times(1)

		cmd := NewActionCmd(client)
		err = SendAction(client, cmd, elp, accAddr.String(), "", 0, false)
		require.Contains(err.Error(), expectedErr.Error())
	})

	client.EXPECT().APIServiceClient().Return(apiServiceClient, nil).AnyTimes()
	client.EXPECT().ReadSecret().Return(passwd, nil).AnyTimes()
	client.EXPECT().Address(gomock.Any()).Return(accAddr.String(), nil).AnyTimes()
	client.EXPECT().Alias(gomock.Any()).Return("producer", nil).AnyTimes()
	client.EXPECT().ReadInput().Return("confirm", nil).AnyTimes()
	client.EXPECT().AskToConfirm(gomock.Any()).Return(true, nil).Times(2)
	client.EXPECT().Config().Return(config.Config{
		Explorer: "iotexscan",
		Endpoint: "testnet1",
	}).AnyTimes()

	apiServiceClient.EXPECT().GetChainMeta(gomock.Any(), gomock.Any()).Return(chainMetaResponse, nil).Times(6)
	apiServiceClient.EXPECT().GetAccount(gomock.Any(), gomock.Any()).Return(accountResponse, nil).Times(4)
	apiServiceClient.EXPECT().SendAction(gomock.Any(), gomock.Any()).Return(&iotexapi.SendActionResponse{}, nil).AnyTimes()

	t.Run("sends signed action to blockchain", func(t *testing.T) {
		cmd := NewActionCmd(client)
		err = SendAction(client, cmd, elp, accAddr.String(), passwd, 1, false)
		require.NoError(err)
	})

	t.Run("send action with nonce", func(t *testing.T) {
		mnemonic := "lake stove quarter shove dry matrix hire split wide attract argue core"
		client.EXPECT().HdwalletMnemonic(gomock.Any()).Return(mnemonic, nil)

		cmd := NewActionCmd(client)
		err = SendAction(client, cmd, elp, "hdw::1/2", passwd, 1, false)
		require.NoError(err)
	})

	t.Run("quit action command", func(t *testing.T) {
		client.EXPECT().AskToConfirm(gomock.Any()).Return(false, nil)

		cmd := NewActionCmd(client)
		err = SendAction(client, cmd, elp, accAddr.String(), passwd, 1, false)
		require.NoError(err)
	})

	t.Run("failed to ask confirm", func(t *testing.T) {
		expectedErr := errors.New("failed to ask confirm")
		client.EXPECT().AskToConfirm(gomock.Any()).Return(false, expectedErr)

		cmd := NewActionCmd(client)
		err = SendAction(client, cmd, elp, accAddr.String(), passwd, 1, false)
		require.Contains(err.Error(), expectedErr.Error())
	})

	t.Run("failed to pass balance check", func(t *testing.T) {
		expectedErr := errors.New("failed to pass balance check")
		apiServiceClient.EXPECT().GetAccount(gomock.Any(), gomock.Any()).Return(nil, expectedErr)

		cmd := NewActionCmd(client)
		err = SendAction(client, cmd, elp, accAddr.String(), passwd, 1, false)
		require.Contains(err.Error(), expectedErr.Error())
	})

	t.Run("failed to get nonce", func(t *testing.T) {
		mnemonic := "lake stove quarter shove dry matrix hire split wide attract argue core"
		expectedErr := errors.New("failed to get nonce")
		client.EXPECT().HdwalletMnemonic(gomock.Any()).Return(mnemonic, nil)
		apiServiceClient.EXPECT().GetAccount(gomock.Any(), gomock.Any()).Return(nil, expectedErr)

		cmd := NewActionCmd(client)
		err = SendAction(client, cmd, elp, "hdw::1/2", passwd, 1, false)
		require.Contains(err.Error(), expectedErr.Error())
	})
	t.Run("failed to get chain meta", func(t *testing.T) {
		expectedErr := errors.New("failed to get chain meta")
		apiServiceClient.EXPECT().GetChainMeta(gomock.Any(), gomock.Any()).Return(nil, expectedErr)
		apiServiceClient.EXPECT().GetAccount(gomock.Any(), gomock.Any()).Return(accountResponse, nil).AnyTimes()

		cmd := NewActionCmd(client)
		err = SendAction(client, cmd, elp, accAddr.String(), passwd, 1, false)
		require.Contains(err.Error(), expectedErr.Error())
	})
}
func TestExecute(t *testing.T) {
	require := require.New(t)
	ctrl := gomock.NewController(t)
	client := mock_ioctlclient.NewMockClient(ctrl)
	apiServiceClient := mock_iotexapi.NewMockAPIServiceClient(ctrl)
	passwd := "123456"
	bytecode := ""

	ks := keystore.NewKeyStore(t.TempDir(), 2, 1)
	acc, err := ks.NewAccount(passwd)
	require.NoError(err)
	accAddr, err := address.FromBytes(acc.Address.Bytes())
	require.NoError(err)

	chainMetaResponse := &iotexapi.GetChainMetaResponse{ChainMeta: &iotextypes.ChainMeta{}}
	elp := createEnvelope(0)
	cost, err := elp.Cost()
	require.NoError(err)
	accountResponse := &iotexapi.GetAccountResponse{AccountMeta: &iotextypes.AccountMeta{
		Address:      accAddr.String(),
		PendingNonce: 1,
		Balance:      cost.String(),
	}}

	client.EXPECT().APIServiceClient().Return(apiServiceClient, nil).Times(13)
	client.EXPECT().Address(gomock.Any()).Return(accAddr.String(), nil).Times(2)
	client.EXPECT().Alias(gomock.Any()).Return("producer", nil).Times(4)
	client.EXPECT().Config().Return(config.Config{
		Explorer: "iotexscan",
		Endpoint: "testnet1",
	}).Times(4)

	apiServiceClient.EXPECT().GetChainMeta(gomock.Any(), gomock.Any()).Return(chainMetaResponse, nil).Times(2)
	apiServiceClient.EXPECT().GetAccount(gomock.Any(), gomock.Any()).Return(accountResponse, nil).Times(5)
	apiServiceClient.EXPECT().SendAction(gomock.Any(), gomock.Any()).Return(&iotexapi.SendActionResponse{}, nil).Times(2)

	client.EXPECT().SelectTranslation(gomock.Any()).Return("action", config.English).Times(7)
	client.EXPECT().SetEndpointWithFlag(gomock.Any()).Times(7)
	client.EXPECT().SetInsecureWithFlag(gomock.Any()).Times(7)
	client.EXPECT().IsCryptoSm2().Return(false).Times(10)
	client.EXPECT().NewKeyStore().Return(ks).Times(4)

	t.Run("failed to get signer address", func(t *testing.T) {
		expectedErr := errors.New("failed to get signer address")
		client.EXPECT().AddressWithDefaultIfNotExist(gomock.Any()).Return("", expectedErr)
		cmd := NewActionCmd(client)
		err = Execute(client, cmd, "test", big.NewInt(100), []byte(bytecode), "100", accAddr.String(), passwd, 0, 100, true)
		require.Error(err, expectedErr)
	})

	client.EXPECT().AddressWithDefaultIfNotExist(gomock.Any()).Return(accAddr.String(), nil).Times(4)

	t.Run("sends signed execution's transaction to blockchain", func(t *testing.T) {
		cmd := NewActionCmd(client)
		err = Execute(client, cmd, "test", big.NewInt(100), []byte(bytecode), "100", accAddr.String(), passwd, 0, 100, true)
		require.NoError(err)
	})

	t.Run("gas limit equals to 0", func(t *testing.T) {
		t.Run("sends signed execution's transaction to blockchain", func(t *testing.T) {
			cmd := NewActionCmd(client)
			apiServiceClient.EXPECT().EstimateActionGasConsumption(gomock.Any(), gomock.Any()).Return(&iotexapi.EstimateActionGasConsumptionResponse{
				Gas: uint64(100),
			}, nil)
			err = Execute(client, cmd, "test", big.NewInt(100), []byte(bytecode), "100", accAddr.String(), passwd, 0, 0, true)
			require.NoError(err)
		})

		t.Run("failed to fix Execution gas limit", func(t *testing.T) {
			expectedErr := errors.New("failed to fix Execution gas limit")
			apiServiceClient.EXPECT().EstimateActionGasConsumption(gomock.Any(), gomock.Any()).Return(nil, expectedErr)
			cmd := NewActionCmd(client)
			err = Execute(client, cmd, "test", big.NewInt(100), []byte(bytecode), "100", accAddr.String(), passwd, 0, 0, true)
			require.Error(err, expectedErr)
		})
	})

	t.Run("failed to get nonce", func(t *testing.T) {
		expectedErr := errors.New("failed to get nonce")
		apiServiceClient.EXPECT().GetAccount(gomock.Any(), gomock.Any()).Return(nil, expectedErr)
		cmd := NewActionCmd(client)
		err = Execute(client, cmd, "test", big.NewInt(100), []byte(bytecode), "100", accAddr.String(), passwd, 0, 100, true)
		require.Error(err, expectedErr)
	})

	t.Run("failed to get gas price", func(t *testing.T) {
		expectedErr := errors.New("failed to get gas price")
		apiServiceClient.EXPECT().SuggestGasPrice(gomock.Any(), gomock.Any()).Return(nil, expectedErr)
		cmd := NewActionCmd(client)
		err = Execute(client, cmd, "test", big.NewInt(100), []byte(bytecode), "", accAddr.String(), passwd, 0, 100, true)
		require.Error(err, expectedErr)
	})

	t.Run("failed to deploy contract with empty bytecode", func(t *testing.T) {
		expectedErr := errors.New("failed to deploy contract with empty bytecode")
		cmd := NewActionCmd(client)
		err = Execute(client, cmd, "", big.NewInt(100), []byte(""), "100", accAddr.String(), passwd, 0, 100, true)
		require.Error(err, expectedErr)
	})
}

func TestRead(t *testing.T) {
	require := require.New(t)
	ctrl := gomock.NewController(t)
	client := mock_ioctlclient.NewMockClient(ctrl)
	apiServiceClient := mock_iotexapi.NewMockAPIServiceClient(ctrl)
	accAddr := identityset.Address(0)
	bytecode := ""

	client.EXPECT().SelectTranslation(gomock.Any()).Return("action", config.English).AnyTimes()
	client.EXPECT().SetEndpointWithFlag(gomock.Any()).AnyTimes()
	client.EXPECT().SetInsecureWithFlag(gomock.Any()).AnyTimes()
	client.EXPECT().APIServiceClient().Return(apiServiceClient, nil).Times(2)

	t.Run("reads smart contract on IoTeX blockchain", func(t *testing.T) {
		client.EXPECT().AddressWithDefaultIfNotExist(gomock.Any()).Return("test", nil)
		apiServiceClient.EXPECT().ReadContract(gomock.Any(), gomock.Any()).Return(&iotexapi.ReadContractResponse{
			Data: "test",
		}, nil)

		result, err := Read(client, accAddr, "100", []byte(bytecode), "test", 100)
		require.NoError(err)
		require.Equal("test", result)
	})

	t.Run("failed to get signer address", func(t *testing.T) {
		expectErr := errors.New("failed to get signer address")
		client.EXPECT().AddressWithDefaultIfNotExist(gomock.Any()).Return("", expectErr)
		_, err := Read(client, accAddr, "100", []byte(bytecode), "test", 100)
		require.Contains(err.Error(), expectErr.Error())
	})
}
